function getAjaxText(url) {
  result = java.ajax(url);
try {
  r = result.match(/\(null,"(.+)"/)[1];
  v = r.replace(/[a-zA-Z]/g,',');
  let arr1 = String(v).split(',');
  let str2 = String.fromCharCode.apply(null, arr1);
